Vue.createApp({data:()=>({enteredQuery:"",isHidden:!0,city:"",temp:"",icon:"",text:"",max:"",min:""}),methods:{makeWeatherDisplay(e){console.log(e),this.isHidden=!1,this.city=e.city,this.temp=e.temp,this.icon=e.icon,this.text=e.text,this.max=e.maxtemp,this.min=e.mintemp},fetchWeatherApi(e){this.enteredQuery?$.post("/weather/js/api",{q:this.enteredQuery},((e,t)=>{this.makeWeatherDisplay(e)})).fail(((e,t)=>{console.log(e.responseJSON.error),this.isHidden=!0,alert(e.responseJSON.error)})):this.isHidden=!0}}}).mount("#weather-display");